package week1.day2;

public class EvenNumber {
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=56537;
		
		if (number%2==0) {
			System.out.println("Even Number :"+number);
			
		} 
		else if(number%2==1)
		{
			System.out.println("Odd Number: "+number);
			
		}
		
		else {
			System.out.println("Not a number");
		}			
		
	}

}
